import React, { useState, useEffect } from 'react';
import './roles.css';
import { render } from '@testing-library/react';
import { Pencil, TrashFill } from 'react-bootstrap-icons';
import firebase from '../../../../initFire';

function Roles() {
    const [users, setUsers] = useState([]);
    useEffect(() => {
        firebase.firestore().collection('users').get().then(response => {
            console.log(response)
//            setUsers(response);
        })
    });
    return (
        <div className="rolesContainer">
            <div className="tableRoles">
                <div className="header">
                    <div>
                        <img src="/assets/usuario.svg" />
                        <span>Usuarios existentes</span>
                    </div>
                    <button>Crear</button>
                </div>
                <div>
                    <table className="tableRolesTable">
                        <thead>
                            <tr>
                                <th>Nombres</th>
                                <th>Apellidos</th>
                                <th>Indentificación (C.C)</th>
                                <th>Rol asociado</th>
                                <th>Estado</th>
                                <th>Teléfono</th>
                                <th>Correo electónico</th>
                                <th>Acción</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.map((user, index) => {
                                return <tr>
                                    <th className="capitalize">{user.data().name}</th>
                                    <th className="capitalize">{user.data().surname}</th>
                                    <th>{user.data().cc}</th>
                                    <th className="capitalize">{user.data().role}</th>
                                    <th className="capitalize">{user.data().state ? 'activo':'inactivo'}</th>
                                    <th>{user.data().phone}</th>
                                    <th>{user.data().email}</th>
                                    <th className="thOptions">
                                        <Pencil size={20} color="#1d43ad"/>
                                        <TrashFill size={20} />
                                    </th>
                                </tr>
                            })}
                        </tbody>
                    </table>
                </div>
            </div>
            <div className="filterMenuRoles">
                <div className="">filtrar</div>
            </div>
        </div>
    )
}

export default Roles;
