import React, { useState, useEffect } from 'react';
import './roles.css';
import { render } from '@testing-library/react';
import { Pencil, TrashFill } from 'react-bootstrap-icons';
import firebase from '../../../../initFire';
import ReactPaginate from 'react-paginate';

function Roles() {
    const [users, setUsers] = useState([]);
    const [activePage, setActivePage] = useState(1);

    useEffect(() => {
        firebase.firestore().collection('users').get().then(querySnapshot => {

            var queryUsers = [];
            querySnapshot.forEach((doc) => {
                queryUsers.push(doc);
            })
            console.log(queryUsers)

            setUsers(queryUsers);
        })
    }, []);
    return (
        <div className="rolesContainer">
            <div className="tableRoles">
                <div className="header">
                    <div>
                        <img src="/assets/usuario.svg" />
                        <span>Usuarios existentes</span>
                    </div>
                    <button>Crear</button>
                </div>
                <div>
                    <table className="tableRolesTable">
                        <thead>
                            <tr>
                                <th>Nombres</th>
                                <th>Apellidos</th>
                                <th>Indentificación (C.C)</th>
                                <th>Rol asociado</th>
                                <th>Estado</th>
                                <th>Teléfono</th>
                                <th>Correo electónico</th>
                                <th>Acción</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.map((user, index) => {
                                return <tr>
                                    <th className="capitalize">{user.data().name}</th>
                                    <th className="capitalize">{user.data().surname}</th>
                                    <th>{user.data().cc}</th>
                                    <th className="capitalize">{user.data().role}</th>
                                    <th className="capitalize">{user.data().state ? 'activo' : 'inactivo'}</th>
                                    <th>{user.data().phone}</th>
                                    <th>{user.data().email}</th>
                                    <th className="thOptions">
                                        <Pencil size={20} color="#1d43ad" />
                                        <TrashFill size={20} />
                                    </th>
                                </tr>
                            })}
                        </tbody>
                    </table>
                    <div>
                        <div>
                        <ReactPaginate
          previousLabel={'previous'}
          nextLabel={'next'}
          breakLabel={'...'}
          breakClassName={'break-me'}
          pageCount={this.state.pageCount}
          marginPagesDisplayed={2}
          pageRangeDisplayed={5}
          onPageChange={this.handlePageClick}
          containerClassName={'pagination'}
          subContainerClassName={'pages pagination'}
          activeClassName={'active'}
        />
                        </div>
                    </div>
                </div>
            </div>
            <div className="filterMenuRoles">
                <div className="">filtrar</div>
            </div>
        </div>
    )
}

export default Roles;
