import React, {useState} from 'react';
import './login.css';
import { render } from '@testing-library/react';
import { Lock, Person, XCircleFill } from 'react-bootstrap-icons';
import $ from 'jquery';
import firebase from '../../initFire';
function Login() {

    const [errorEmail, setErrorEmail] = useState(false);
    const [errorPassword, setErrorPassword] = useState(false);
    
    return (
        <div className="containerLogin">
            <div className="bgImgLogin" >
            </div>
            <div className="mainContainerLogin">
                <div className="welcomeTitleLogin">
                    <h5>Aplicación <br /> OLSoftware</h5>
                    <span>Prueba prácicta Front-end Senior</span>
                </div>
                <div className="containerFormLogin">
                    <form onSubmit={
                        (e) => {
                            e.preventDefault();
                            e.stopPropagation();
                            login();
                        }} id="formLogin">
                        <h5>Inicio sesión</h5>
                        <div className="inputsLoginContainer">
                            <div className="inputLogin">
                                <input type="text" name="email" className="form-control" placeholder="Usuario" />
                                {!errorEmail ? <Person size={24} color="#555555" /> : <XCircleFill size={24} color="#ff0000" />}
                            </div>
                            <div className="inputLogin">
                                <input type="password" name="password" className="form-control" placeholder="Contraseña" />
                                {!errorPassword ? <Lock size={24} color="#555555" /> : <XCircleFill size={24} color="#ff0000" />}
                            </div>
                            {errorPassword || errorEmail ? <span id="errorLoginText"></span>:''}
                        </div>
                        <button type="submit" className="btn btn-primary">Iniciar sesión</button>
                    </form>
                </div>

            </div>
            <div className="olsMark">
                OLSoftware - 2018
            </div>
            <div className="modal" tabindex="-1" role="dialog" id="modalLoginOpen" data-backdrop="static">
                <div className="modal-dialog modal-dialog-centered">
                    <div>Estamos preparando todo para tí</div>
                    <div className="dotsLoadingContainer">
                        <div className="dotLoading"></div>
                        <div className="dotLoading"></div>
                        <div className="dotLoading"></div>
                    </div>
                </div>
            </div>
            <div className="modal" tabindex="-1" role="dialog" id="modalLogin">
                <div className="modal-dialog modal-dialog-centered">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title">Modal title</h5>
                            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div className="modal-body">
                            <p>Modal body text goes here.</p>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-primary">Save changes</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
    function login() {
        var loginF = $('#formLogin').serializeArray().reduce(function (obj, item) {
            obj[item.name] = item.value;
            return obj;
        }, {});
        firebase.auth().signInWithEmailAndPassword(loginF.email, loginF.password).then(response =>{

        }).catch(err=>{

        });
    }
}

export default Login;
