import React from 'react';
import './login.css';
import { render } from '@testing-library/react';
import { Lock, Person } from 'react-bootstrap-icons';
import $ from 'jquery';

class Login extends React.Component() {
    constructor(props){
        this.login = this.login.bind(this);
    }
    render() {
        return <div className="containerLogin">
            <div className="bgImgLogin" >
            </div>
            <div className="mainContainerLogin">
                <div className="welcomeTitleLogin">
                    <h5>Aplicación <br /> OLSoftware</h5>
                    <span>Prueba prácicta Front-end Senior</span>
                </div>
                <div className="containerFormLogin">
                    <form onSubmit={
                        (e) => {
                            e.preventDefault();
                            e.stopPropagation();
                            login();
                        }}>
                        <h5>Inicio sesión</h5>
                        <div className="inputsLoginContainer">
                            <div className="inputLogin">
                                <input type="text" name="email" className="form-control" placeholder="Usuario" />
                                <Person size={24} color="#555555" />
                            </div>
                            <div className="inputLogin">
                                <input type="password" name="password" className="form-control" placeholder="Contraseña" />
                                <Lock size={24} color="#555555" />
                            </div>
                        </div>
                        <button type="submit" className="btn btn-primary">Iniciar sesión</button>
                    </form>
                </div>

            </div>
            <div className="olsMark">
                OLSoftware - 2018
        </div>
            <div className="modal" tabindex="-1" role="dialog" id="modalLogin">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title">Modal title</h5>
                            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div className="modal-body">
                            <p>Modal body text goes here.</p>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-primary">Save changes</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    }
    login() {
    $('#modalLogin').modal('show')
}
}

export default Login;
